import { ACTION_TYPES } from '../constants';
const initialState = {
    products: [],
    specUnitSelection:  []
}

export default (state = initialState, { type, payload }) => {
  switch (type) {

  case ACTION_TYPES.REQUEST.SET_PRODUCT:
    return { ...state, products: payload }
  
  case ACTION_TYPES.REQUEST.SET_SPEC_SELECTION: 
    return {...state, specUnitSelection: payload}
  
  default:
    return state
  }
}
