import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Card, CardHeader, CardFooter, CardBody, Form, FormGroup, Label, Input, Row, Col} from 'reactstrap';
import {Button, Table} from 'antd'
import DatePicker from 'react-datepicker';

class CreateBill extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dateCreated: new Date(),
      dataSource: []
    }
  }

  render() {
    return (
      <Card>
        <CardHeader>
          <strong>Lập Hóa Bán Sản Phẩm</strong>
        </CardHeader>
        <CardBody>
          <Card>
            <CardBody>
              <Form className={'form-inline'}>
                <Row>
                  <Col>
                    <FormGroup>
                      <Label for={'dateCreated'} className='pr-1'>Ngày Tạo</Label>
                      <DatePicker
                        selected={this.state.dateCreated}
                        className="form-control"
                        dateFormat="dd/MM/yyyy"
                        name='dateCreated'
                        disabled={true}/>
                    </FormGroup>
                  </Col>
                  <Col>
                    <FormGroup>
                      <Label for={'productName'} className='pr-1'>Tên Sản Phẩm</Label>
                      <Input type={'text'} name={'productName'} id={'productName'}/>
                    </FormGroup>
                  </Col>
                  <Col>
                    <FormGroup>
                      <Label for={'productName'} className='pr-1'>Đơn vị</Label>
                      <Input type={'text'} name={'productName'} id={'productName'}/>
                    </FormGroup>
                  </Col>
                  <Col>
                    <FormGroup>
                      <Label for={'productPrice'} className='pr-1'>Giá Sản Phẩm</Label>
                      <Input type={'text'} name={'productPrice'} id={'productPrice'}/>
                    </FormGroup>
                  </Col>
                  <Col>
                    <FormGroup className={'text-bottom'}>
                      <Button type={'primary'} icon={'plus'} className={'mt-4'}>Thêm Sản Phẩm</Button>
                    </FormGroup>
                  </Col>
                </Row>
              </Form>
            </CardBody>
          </Card>
          <Row>
            <Col md={{ size: 4, order: 3, offset: 8 }}>
              <FormGroup row>
                <Label>Thành tiền</Label>
                <Input type={'text'} value={this.state.total} disabled inline={'true'}/>
              </FormGroup>
            </Col>
          </Row>
          <Table bordered={true} dataSource={this.state.dataSource} rowKey='key'>
            <Table.Column
              title={'STT'}
              dataIndex={'key'}
              key={'key'}/>
            <Table.Column
              title={'Tên Sản Phẩm'}
              dataIndex={'productName'}
              key={'productName'}/>
            <Table.Column
              title={'Đơn vị'}
              dataIndex={'unit'}
              key={'unit'}/>
            <Table.Column
              title={'Đơn Giá'}
              dataIndex={'price'}
              key={'price'}/>
          </Table>
        </CardBody>
        <CardFooter>

        </CardFooter>
      </Card>
    );
  }
}

function mapStateToProps(state) {
  return {};
}

export default connect(
  mapStateToProps,
)(CreateBill);
