import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Card, CardHeader, CardBody} from "reactstrap";
import { Table, Popconfirm, Button, Modal } from "antd";
import {getBillRequest, getDetail} from "../../../actions/acceptAction";

class Accept extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      visible: false,
    }
  }

  async componentWillMount() {
    await this.props.onGetBillRequest(this.props.authenticationReducer.branch);
  }

  showDetail = (text, record) => {
    this.props.onGetDetail(record.id);

  }

  handleDelete = (id) => {
    console.log(id);
  }

  handleCancel = () => {
    this.setState({ visible: false });
  }
  handleOk = () => {
    this.setState({ loading: true });
    setTimeout(() => {
      this.setState({ loading: false, visible: false });
    }, 3000);
  }
  render() {
    const {billsRequest} = this.props.acceptReducer;
    const { visible, loading } = this.state;
    return (
      <div>
        <Card>
          <CardHeader>
            <strong>Xác Nhận Yêu Cầu</strong>
          </CardHeader>
          <CardBody>
            <Table
              rowKey='id'
              bordered={true}
              dataSource={billsRequest}
            >
              <Table.Column
                title={'Mã Yêu Cầu'}
                dataIndex={'id'}
                key={'id'}/>
              <Table.Column
                title={'Chi Nhánh Yêu Cầu'}
                dataIndex={'branch'}
                key={'branch'}/>
              <Table.Column
                title={'Ngày Yêu Cầu'}
                dataIndex={'dateRequested'}
                key={'dateRequested'}/>
              <Table.Column
                title={'Hành Động'}
                dataIndex={'operation'}
                key={'operation'}
                render={(text, record) => (
                  billsRequest.length > 0 ? (
                    <span>
                     <Button htmlType='button' type="primary" icon={'info-circle'} onClick={() => this.showDetail(text, record)}/>
                      <Popconfirm title="Bạn Thật Sự Muốn Xóa" onConfirm={() => this.handleDelete(record.id)}>
                        <Button htmlType='button'  icon={'close-circle'} type="danger"/>
                      </Popconfirm>
                    </span>
                    ) : null
                )}
              />
            </Table>
          </CardBody>
        </Card>
        <Modal
          visible={visible}
          title="Chi tiết Phiếu Yêu Cầu"
          onOk={this.handleOk}
          onCancel={this.handleCancel}
          footer={[
            <Button key="back" onClick={this.handleCancel}>Return</Button>,
            <Button key="submit" type="primary" loading={loading} onClick={this.handleOk}>
              Submit
            </Button>,
          ]}
        >
          <p>Some contents...</p>
          <p>Some contents...</p>
          <p>Some contents...</p>
          <p>Some contents...</p>
          <p>Some contents...</p>
        </Modal>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    authenticationReducer: state.auth,
    acceptReducer: state.acceptReducer
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onGetBillRequest: (branch) => {
      return dispatch(getBillRequest(branch));
    },
    onGetDetail: (id) =>  {
      return dispatch(getDetail(id));
    }
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Accept);
