import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Card} from "reactstrap";
import CardHeader from "reactstrap/es/CardHeader";
import CardBody from "reactstrap/es/CardBody";
import {Table} from "antd";
import Button from "reactstrap/es/Button";
import {getBillRequest} from "../../../actions/acceptAction";
import Column from "antd/lib/table/Column";

class Accept extends Component {
  constructor(props) {
    super(props);
    this.state = {
      billsRequest: []
    }
  }

  async componentWillMount() {
    await this.props.onGetBillRequest(this.props.authenticationReducer.branch);
  }

  showDetail = (text, record) => {
    console.log(text, record);
  }

  render() {
    return (
      <div>
        <Card>
          <CardHeader>
            <strong>Xác Nhận Yêu Cầu</strong>
          </CardHeader>
          <CardBody>
            <Table
              rowKey='id'
              bordered={true}
              dataSource={this.props.acceptReducer.billsRequest}
            >
              <Column
                title={'Mã Yêu Cầu'}
                dataIndex={'id'}
                key={'id'}/>
              <Column
                title={'Chi Nhánh Yêu Cầu'}
                dataIndex={'branch'}
                key={'branch'}/>
              <Column
                title={'Ngày Yêu Cầu'}
                dataIndex={'dateRequested'}
                key={'dateRequested'}/>
              <Column
                title={'Hành Động'}
                dataIndex={'operation'}
                key={'operation'}
                render={(text, record) => (
                  <span>
                     <a href="javascript:;">Delete</a>
                    {/*<Button onClick={this.showDetail(text, record)}>ShowDetail</Button>*/}
                  </span>
                )}
              />
            </Table>
          </CardBody>
        </Card>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    authenticationReducer: state.auth,
    acceptReducer: state.acceptReducer
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onGetBillRequest: (branch) => {
      return dispatch(getBillRequest(branch));
    }
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Accept);
