import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Card, CardHeader, CardBody} from "reactstrap";
import {Table, Popconfirm, Button, Modal, Form} from "antd";
import {getBillRequest, getDetail, accept} from "../../../actions/acceptAction";

class Accept extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      visible: false,
      billRequestId:'',
      isSeen: true,

    }
  }

  async componentWillMount() {
    await this.props.onGetBillRequest(this.props.authenticationReducer.branch);
  }

  showDetail = async (text, record) => {
    await this.props.onGetDetail(record.id);
    await this.setState({billRequestId:record.id});
    this.showModal();
  }

  handleDelete = (id) => {
    console.log(id);
  }

  handleCancel = () => {
    this.setState({visible: false});
  }
  handleOk = async () => {
    await this.setState({loading: true});
    await this.props.onAccept(this.state.billRequestId);
    this.setState({loading: false, visible: false});
  }
  showModal = () => {
    this.setState({
      visible: true,
    });
  }
  handleToggle = prop => (enable) => {
    this.setState({ [prop]: enable });
  }
  handleSearch = async () => {
    const condition = await {
      isSeen: this.state.isSeen
    };
    this.props.onGetBillRequest(this.props.authenticationReducer.branch, condition);
  }
  render() {
    console.log(this.props.acceptReducer);
    const {billsRequest, detailRequest} = this.props.acceptReducer;
    const {visible, loading, isSeen} = this.state;
    return (
      <div>
        <Card>
          <CardHeader>
            <strong>Xác Nhận Yêu Cầu</strong>
          </CardHeader>
          <CardBody>
            <Form layout={'inline'}>
              <Form.Item label={'Chưa xem'}>
                <Switch checked={isSeen} onChange={this.handleToggle('isSeen')} />
              </Form.Item>
              <Button htmlType={'button'} onClick={this.handleSearch}/>
            </Form>
            <Table
              rowKey='id'
              bordered={true}
              dataSource={billsRequest}
            >
              <Table.Column
                title={'Mã Yêu Cầu'}
                dataIndex={'id'}
                key={'id'}/>
              <Table.Column
                title={'Chi Nhánh Yêu Cầu'}
                dataIndex={'branch'}
                key={'branch'}/>
              <Table.Column
                title={'Ngày Yêu Cầu'}
                dataIndex={'dateRequested'}
                key={'dateRequested'}/>
              <Table.Column
                title={'Hành Động'}
                dataIndex={'operation'}
                key={'operation'}
                render={(text, record) => (
                  billsRequest.length > 0 ? (
                    <span>
                     <Button htmlType='button' type="primary" icon={'info-circle'}
                             onClick={() => this.showDetail(text, record)}/>
                      <Popconfirm title="Bạn Thật Sự Muốn Xóa" onConfirm={() => this.handleDelete(record.id)}>
                        <Button htmlType='button' icon={'close-circle'} type="danger"/>
                      </Popconfirm>
                    </span>
                  ) : null
                )}
              />
            </Table>
          </CardBody>
        </Card>
        <Modal
          visible={visible}
          title="Chi tiết Phiếu Yêu Cầu"
          onOk={this.handleOk}
          onCancel={this.handleCancel}
          footer={[
            <Button key="back" onClick={this.handleCancel}>Return</Button>,
            <Button key="submit" type="primary" loading={loading} onClick={this.handleOk}>
              Submit
            </Button>,
          ]}
        >
          <Table
            rowKey='product'
            bordered={true}
            dataSource={detailRequest}
          >
            <Table.Column
              title={'Tên Sản Phẩm'}
              dataIndex={'product'}
              key={'product'}/>
            <Table.Column
              title={'Số lượng'}
              dataIndex={'amount'}
              key={'amount'}/>
            <Table.Column
              title={'Đơn vị'}
              dataIndex={'unit'}
              key={'unit'}/>
          </Table>
        </Modal>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    authenticationReducer: state.auth,
    acceptReducer: state.acceptReducer
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onGetBillRequest: (branch, condition) => {
      return dispatch(getBillRequest(branch, condition));
    },
    onGetDetail: (id) => {
      return dispatch(getDetail(id));
    },
    onAccept: (id) => {
      return dispatch(accept(id));
    }
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Accept);
